% characteristic equationfunction y = r2reig(b)global L ro J0 Jp Mps = sin(b*L);c = cos(b*L);sh = sinh(b*L);ch = cosh(b*L);y = (s.*ch-c.*sh) ...   +2*Mp*(b/ro.*(s.*sh)) ...   +J0*b.^3/ro.*(c.*ch+1) ...   +2*Jp*(b.^3/ro.*(c.*ch)) ...   +Mp*(J0+Jp)*(b.^4/ro^2.*(c.*sh-s.*ch)) ...   +J0*Jp*(b.^6/ro^2.*(-s.*ch-c.*sh)) ...   +J0*Jp*Mp*(b.^7/ro^3.*(1-c.*ch));