% N solutions of the characteristic equation% the characteristic equation is the function  r2reig.m function y = r2rfind(N)global L ro J0 Jp Mpbi = 0;bf = 1;for i = 1:N;   h = 0;   while h<1,      db = [bi:(bf-bi)/100:bf];      if min(r2reig(db))<0 & max(r2reig(db))>0,         sol(i) = fzero('r2reig',(bi+bf)/2); %fzero('r2reig',[bi bf]);         bi = sol(i)+0.001;		 bf = bi+1;         h = 1;      else         bi = bf;         bf = bi+1;      end   endendy = sol;